import React from 'react'
import Service from './service'

class ShowData extends React.Component {
    // constructor() {
    //     super();
    //     this.state = {
    //         ListData: []

    //     }
    // }

    // loadData = async () => {
    //     const dataAPI = await Service.summary();

    //     this.setState({
    //         ListData: dataAPI
    //     });

    //     console.log(this.state.ListData)


    // }


        constructor(props) {
          super(props);
          this.state = {
            ListData: []
          };
        }

        componentDidMount() {
            fetch("http://localhost:8080/api/todo")
              .then(res => res.json())
              .then(parsedJSON => parsedJSON.results.map(data => (
                {
                  name: `${data.name}`,
                  activity: `${data.activity}`,
                  date: `${data.date}`,

                }
              )))
              .then(ListData => this.setState({
                ListData,
                isLoaded: false
              }))
              .catch(error => console.log('parsing failed', error))
          }

    render() {
        return (
            <div>
                <table className="table table-bordered table-hover table-striped dataTable">
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>Name</th>
                            <th>Activity</th>
                            <th>Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.ListData.map(data => {
                                return (
                                    <tr>
                                        <td>{data.name}</td>
                                        <td>{data.activity}</td>
                                        <td>{data.date}</td>

                                    </tr>
                                )
                            })
                        }
                    </tbody>
                </table>
            </div>
        )
    }
}

export default ShowData
