import React from 'react'
import Service from './service'

class ShowData extends React.Component {
    constructor() {
        super();
        this.state = {
            ListData: []

        }
    }

    loadData = async () => {
        const dataAPI = await Service.summary();

        this.setState({
            ListData: dataAPI
        });

        console.log(this.state.ListData)


    }


    render() {
        return (
            <div>
<Table id="category">
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>Activity</th>
                        <th>Date</th>  
                        <th>Action</th>

                    </tr>
                    {
                        this.state.ListData.map(data => {
                            return (
                                <tr>
                                    <td>{data.id}</td>
                                    <td>{data.name}</td>
                                    <td>{data.activity}</td>
                                    <td>{data.date}</td>
                                    <td><Button style={{margin:"0 10px 0 0"}} color="warning" onClick={() => this.hendleEdit(data.id)}>Edit</Button> 
                                    <Button color="danger" onClick={() => this.hendleDelete(data)}>Delete</Button></td>
                                    </tr>
                            )
                        })
                    }
                </Table> 
            </div>
        )
    }
}

export default ShowData
