import React from 'react';
import { Button, Form, Label, Input } from 'reactstrap';






class ShowData extends React.Component {
    state = {
        name: "",
        activity:"",
        date:""
    }

    onChange = (e) => this.setState({ [e.target.name]: e.target.value});

    onSubmitTodoList = (e) => {
        if(this.state.name==""| this.state.activity==""| this.state.date=="" ) return;
        this.props.addTodoList(this.state.name,this.state.activity,this.state.date);

        this.setState({

            name: "",
            activity:"",
            date:""
        });
    }
    render() {
        const { handlerCancel, functionChange ,  functionSave, categoryModel} = this.props;
        return (
            <div>
                <p>Create Data</p>
                <Form>

           <Label>Name</Label>
                  <Input type="text" class="form-control" placeholder="Name" value={this.state.name} onChange={functionChange('name')}/> <br />
                <Label>Activity</Label>
                <Input type="text" class="form-control" placeholder="Activity"  value={this.state.activity} onChange={functionChange('activity')}/> <br />
                <Label>Date</Label> <br/>
                <Input type="date" class="form-control" value={this.state.date} onChange={functionChange('date')}/> <br />
                <br/>
                <Button type="submit" color="primary" class="btn btn-info pull-right"  onClick={functionSave}>Save Change</Button> <br/><br/>
                <Button type="submit" color="danger" class="btn btn-default" onClick={handlerCancel}>Cancel</Button>
                </Form>

</div>
        )
    }
}

export default ShowData
