import React from 'react';
import { Button, Form, Label, Input } from 'reactstrap';

var http = require('http');
var Client = require('mariasql')
var pug = require('pug');
var qs = require('querystring');
var url = require('url');

var listPug = './templates/list.pug';
var addFormPug = './templates/addform.pug';
var editFormPug = './templates/editform.pug';

var db = new Client({
   host: 'localhost',
   user: 'root',
   password: '',
   db: 'activity_db'
});

var server = http.createServer(function (req, res) {
   res.writeHead(200, {'Content-Type': 'text/html'});
   if (req.url === '/') {
      db.query('SELECT * FROM buku', function (error, result) {
         if (error) throw error;
         var template = pug.renderFile(listPug, {books: result});
         res.end(template);
      });
      db.end();
   } else if (req.url === '/add') {
      switch (req.method) {
	     case 'GET':
		    var template = pug.renderFile(addFormPug);
		    res.end(template);
		    break;		 
		 case 'POST':
		    var body = '';

		    req.on('data', function (data) { 
			   body += data;
			});

			req.on('end', function () { 
			   var form = qs.parse(body);
			   var newRow = [
			      form['buku_id'],
			      form['buku_judul'],
			      form['buku_penulis'],
			      form['buku_penerbit']
			   ];
			   var sql = 'INSERT INTO buku VALUES(?,?,?,?)';
			   db.query(sql, newRow, function (error, result) {
			      if (error) throw error;
			      // kode untuk redirect ke root document
			      res.writeHead(302,{'Location': '/'});
			      res.end();
			   });
			});

		    break;		 
	  }
   } else if (url.parse(req.url).pathname === '/edit') {
      switch (req.method) {
	     case 'GET':
		    var id = qs.parse(url.parse(req.url).query).id;
		    var sql = 'SELECT * FROM buku WHERE buku_id = ?';
		    db.query(sql, [id], function (error, result) {
            if (error) throw error;
               var template = pug.renderFile(editFormPug, {book: result[0]});
               res.end(template);
            });
            db.end();
		    break;		 
		 case 'POST':
		    var body = '';

		    req.on('data', function (data) { 
			   body += data;
			});

			req.on('end', function () { 
			   var form = qs.parse(body);
			   var params = [			      
			      form['buku_judul'],
			      form['buku_penulis'],
			      form['buku_penerbit'],
			      form['buku_id']
			   ];
			   var sql = `UPDATE buku 
			              SET
			                 buku_judul = ?,
			                 buku_penulis = ?,
			                 buku_penerbit = ?
			              WHERE
			                 buku_id = ?`;
			   db.query(sql, params, function (error, result) {
			      if (error) throw error;
			      // kode untuk redirect ke root document
			      res.writeHead(302,{'Location': '/'});
			      res.end();
			   });
			});

		    break;		 
	  }
   } else if (url.parse(req.url).pathname === '/delete') {
      // mengambil nilai dari parameter id
      var id = qs.parse(url.parse(req.url).query).id;
      var sql = 'DELETE FROM buku WHERE buku_id = ?';
	  db.query(sql, [id], function (error, result) {
	     if (error) throw error;
		 // kode untuk redirect ke root document
		 res.writeHead(302,{'Location': '/'});
		 res.end();
	  });
   }
});

server.listen(3000);
console.log('Server aktif di http://localhost:3000');



class FormInput extends React.Component {

    render() {
        const { handlerCancel, functionChange ,  functionSave, categoryModel} = this.props;
        return (
            <div>
                <p>Create Data</p>
                <Form>

           <Label>Name</Label>
                  <Input type="text" class="form-control" placeholder="Name" value={categoryModel.name} onChange={functionChange('name')}/> <br />
                <Label>Activity</Label>
                <Input type="text" class="form-control" placeholder="Activity"  value={categoryModel.activity} onChange={functionChange('activity')}/> <br />
                <Label>Date</Label> <br/>
                <Input type="date" class="form-control" value={categoryModel.date} onChange={functionChange('date')}/> <br />
                <br/>
                <Button type="submit" color="primary" class="btn btn-info pull-right"  onClick={functionSave}>Save Change</Button> <br/><br/>
                <Button type="submit" color="danger" class="btn btn-default" onClick={handlerCancel}>Cancel</Button>
                </Form>

</div>
        )
    }
}

export default FormInput
