import React, {Component} from 'react';
import { Button, Form, Label, Input } from 'reactstrap';

handlerCreate = () => {

    const idMax = this.state.ListData.length;

    this.setState({
        isShow: true,
        mode: 'create',
        categoryModel: {
            ...this.state.categoryModel,
            ['id']: idMax + 1
        }
    })
}
class Login extends Component {
    render() {
       return (
            <div>
                 <h2>Login Form</h2>
                    <Form>


                        <Label><b>Username</b></Label>
                        <Input type="text" placeholder="Enter Username" name="uname" required/>

                        <Label><b>Password</b></Label>
                        <Input type="password" placeholder="Enter Password" name="psw" required/> <br/>

                        <Button type="submit" style={{margin:"10px 10px 10px 0"}} data-toggle="modal" data-target="#modal-default" onClick={this.handlerCreate}>Login</Button>
                        <Button type="Button" className="cancelbtn">Cancel</Button> <br/>
                        <span className="psw">Forgot <a href="#">password?</a></span>
                    </Form>
            </div>
        )
    }

}

export default Login
