import React from 'react';
import './css/App.css';
import Header from './component/Header';
import Login from './component/login/Login';
import FormInput from './component/input/input';
import { Button,  Table } from 'reactstrap';
import uuid from 'uuid';
import axios from 'axios';

class App extends React.Component {

     itemModel = {
         id: 0,
         name: '',
         activity: '',
         date: ''
     }

     constructor() {
         super();
         this.state = {
             ListData:
                 [{ id: 1, name: 'FSH', activity: 'Fashion', date: '19 September 2020'},
                 { id: 2, name: 'FOO', activity: 'Food', date: '19 September 2020'},
                 { id: 3, name: 'ELC', activity: 'Electronic', date: '19 September 2020'},
                 { id: 4, name: 'FUR', activity: 'Furniture', date: '19 September 2020' },
                 { id: 5, name: 'DRN', activity: 'Drink', date: '19 September 2020' },

                 ],
             isShow: false,
             categoryModel: this.itemModel,
             mode: ''
         }
     }



     hendleEdit = (id) => {
         const selected = this.state.ListData.find(a => a.id === id);

         this.setState({
             isShow: true,
             mode: 'edit',
             categoryModel: selected
         })
     }



     hendleDelete = (data) => {
         const { ListData } = this.state;
         const IndexData = ListData.findIndex(dt => dt.id === data.id);
         if (window.confirm(`Apakah anda yakin untuk menghapus data ${data.activity}`)) {
             ListData.splice(IndexData, 1);
         }

         this.setState({
             ListData: ListData
         })

     }

     handlerCreate = () => {

        const idMax = this.state.ListData.length;

        this.setState({
            isShow: true,
            mode: 'create',
            categoryModel: {
                ...this.state.categoryModel,
                ['id']: idMax + 1
            }
        })
    }
    onLogin = (req, res ) => {
        var user_name=req.body.email;
        var password=req.body.password;
        if(user_name=='admin' && password=='admin'){
            {this.state.isShow ?
                <FormInput handlerCancel={this.handlerCancel} functionChange={this.functionChange}
                    functionSave={this.functionSave}
                    categoryModel={this.state.categoryModel} />
                : null} 
        }
        else{
            res.send('Failure');
        }
      } 
      functionChange = activity => ({ target: { value } }) => {
        this.setState({
            categoryModel: {
                ...this.state.categoryModel,
                [activity]: value
            }
        })
    }
    handlerCancel = () => {

        this.setState({
            isShow: false
        })
    }

    functionSave = () => {
        const list = this.state.ListData;

        if (this.state.mode === 'create') {
            list.push(this.state.categoryModel);
        }
        else {
            const IndexData = list.findIndex(dt => dt.id === this.state.categoryModel.id);
            this.state.ListData[IndexData] = {
                id: this.state.categoryModel.id,
                name: this.state.categoryModel.name,
                activity: this.state.categoryModel.activity,
                date:this.state.categoryModel.date
            }
        }
        this.setState({
            ListData: list,
            isShow: false
        })

    }





    render() {
        return (
             <div>
                 <Header />
                 <p>Data Category</p>
                 {/* {JSON.stringify(this.state.categoryModel)} */}

                 {/* {this.state.isShow ?
                     <FormInput handlerCancel={this.handlerCancel} functionChange={this.functionChange}
                         functionSave={this.functionSave}
                         categoryModel={this.state.categoryModel} />
                     : null} <br /> */}


                 {/* <Button style={{margin:"0 0 30px 0"}} color="success" className="btn btn-primary" data-toggle="modal" data-target="#modal-default" 
                         onClick={this.handlerCreate}>Create New </Button>
                 <hr style={{margin:"0 0 30px 0"}}/> */}

<div>
          <form className="form-signin">
            <h2 className="form-signin-heading">Please sign in</h2>
            <label for="inputEmail" className="sr-only">Email address</label>
            <input type="text"  className="form-control" placeholder="User Name" required autofocus />
            <label for="inputPassword" className="sr-only">Password</label>
            <input type="password"  className="form-control" placeholder="Password" required />

            <button className="btn btn-lg btn-primary btn-block" onClick={this.onLogin} type="button">Sign in</button>
          </form>
          <div>
          </div>
        </div>


                 <Table id="category">
                     <tr>
                         <th>No</th>
                         <th>Name</th>
                         <th>Activity</th>
                         <th>Date</th>  
                         <th>Action</th>

                     </tr>
                     {
                         this.state.ListData.map(data => {
                             return (
                                 <tr>
                                     <td>{data.id}</td>
                                     <td>{data.name}</td>
                                     <td>{data.activity}</td>
                                     <td>{data.date}</td>
                                     <td><Button style={{margin:"0 10px 0 0"}} color="warning" onClick={() => this.hendleEdit(data.id)}>Edit</Button> 
                                     <Button color="danger" onClick={() => this.hendleDelete(data)}>Delete</Button></td>
                                 </tr>
                             )
                         })
                     }
                 </Table>
             </div>
        //     <div >
        //     <Header />
        //     <ShowData/>
        //   </div>
        )
    }
}

export default App;
