import React from 'react';
import './css/App.css'
import FormInput from './component/input/input';
import { Button,  Table } from 'reactstrap';
import Service from './component/service';

class App extends React.Component {
    constructor() {
        super();
        this.state = {
            ListData:
                [

                ],
            isShow: false,
            categoryModel: this.itemModel,
            mode: '',
            error: {}
        }
    }
    componentDidMount() {
        this.loadList();
    }

    loadList = async () => {
        const respon = await Service.getAll();
        if (respon.success) {
            this.setState({
                ListData: respon.result
            })
        }
    }
    handlerCreate = () => {

        this.setState({
            mode: 'create',
            categoryModel: this.itemModel
        })
    }

    hendleEdit = async (id) => {
        const respon = await Service.getById(id);

        if (respon.success) {
            this.setState({
                mode: 'edit',
                categoryModel: respon.result
            })
        }
    }

    handlerCancel = () => {

        this.setState({
            isShow: false
        })
    }

    hendleDelete = async (data) => {

        if (window.confirm(`Apakah anda yakin untuk menghapus data ${data.name}`)) {
            const respon = await Service.Delete(data)
            if (respon.success) {
                alert('Success : ' + respon.result);
                this.loadList();
            }
        }

    }

    functionChange = name => ({ target: { value } }) => {
        this.setState({
            categoryModel: {
                ...this.state.categoryModel,
                [name]: value
            }
        })
    }


    functionSave = async () => {
        const { categoryModel, mode } = this.state;

        if (mode === 'create') {
            if (this.functionValidasi()) {
                const respon = await Service.Create(categoryModel);
                if (respon.success) {
                    alert('Success : ' + respon.result);
                    this.loadList();
                }
                else {
                    alert('Error :', respon.result);
                }
            }
        }

    }


    render() {
        return (
            <div>
                <p>Data Category</p>

                {this.state.isShow ?
                    <FormInput handlerCancel={this.handlerCancel} functionChange={this.functionChange}
                        functionSave={this.functionSave}
                        categoryModel={this.state.categoryModel} />
                    : null} <br />

                <Button style={{margin:"0 0 30px 0"}} color="success" className="btn btn-primary" data-toggle="modal" data-target="#modal-default" 
                        onClick={this.handlerCreate}>Create New </Button>
                <hr style={{margin:"0 0 30px 0"}}/>
                <Table id="category">
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>Activity</th>
                        <th>Date</th>  
                        <th>Action</th>

                    </tr>
                    {
                        this.state.ListData.map(data => {
                            return (
                                <tr>
                                    <td>{data.id}</td>
                                    <td>{data.name}</td>
                                    <td>{data.activity}</td>
                                    <td>{data.date}</td>
                                    <td><Button style={{margin:"0 10px 0 0"}} color="warning" onClick={() => this.hendleEdit(data.id)}>Edit</Button> 
                                    <Button color="danger" onClick={() => this.hendleDelete(data)}>Delete</Button></td>
                                    </tr>
                            )
                        })
                    }
                </Table>
            </div>
        )
    }
}

export default App;
