class Login extends React.Component {
    onLogin = (req, res) => {
        var user_name=req.body.email;
        var password=req.body.password;
        if(user_name=='admin' && password=='admin'){
            res.send('success');
        }
        else{
            res.send('Failure');
        }
      }
    render() {
      return (
        <div>
          <form className="form-signin">
            <h2 className="form-signin-heading">Please sign in</h2>
            <label for="inputEmail" className="sr-only">Email address</label>
            <input type="text" onChange={this.handleEmailChange} id="inputEmail" className="form-control" placeholder="Email address" required autofocus />
            <label for="inputPassword" className="sr-only">Password</label>
            <input type="password" onChange={this.handlePasswordChange} id="inputPassword" className="form-control" placeholder="Password" required />

            <button className="btn btn-lg btn-primary btn-block" onClick={this.onLogin} type="button">Sign in</button>
          </form>
          <div>
          </div>
        </div>

      )
    }
}

export default Login
