import React from 'react';
import { Button, Form, Label, Input } from 'reactstrap';



const express = require('express');
const mysql = require('mysql');
const app = express();

app.use(express.static('public'));
app.use(express.urlencoded({extended: false}));

const connection = mysql.createConnection({
  host: 'localhost',
  user: 'root',
  password: '',
  database: 'activity_db'
});

app.get('/', (req, res) => {
  res.render('App.js');
});

app.get('/index', (req, res) => {
  connection.query(
    'SELECT * FROM todolist_tbl',
    (error, results) => {
      res.render('index.ejs', {items: results});
    }
  );
});

app.get('/new', (req, res) => {
  res.render('new.ejs');
});

app.post('/create', (req, res) => {
  connection.query(
    'INSERT INTO todolist_tbl (name) VALUES (?)',
    [req.body.itemName],
    (error, results) => {
      res.redirect('/index');
    }
  );
});

app.post('/delete/:id', (req, res) => {
  connection.query(
    'DELETE FROM todolist_tbl WHERE id = ?',
    [req.params.id],
    (error, results) => {
      res.redirect('/index');
    }
  );
});

app.get('/edit/:id', (req, res) => {
  connection.query(
    'SELECT * FROM todolist_tbl WHERE id = ?',
    [req.params.id],
    (error, results) => {
      res.render('edit.ejs', {item: results[0]});
    }
  );
});

app.post('/update/:id', (req, res) => {
  // Ketik code untuk memperbarui item yang dipilih
  connection.query(
    'UPDATE todolist_tbl SET name = ? WHERE id = ?',
    [req.body.itemName, req.params.id],
    (error, results) => {
      res.redirect('/index');
    }
  );
  // Hapus code pengalihan ke halaman daftar

});

app.listen(3000);



class FormInput extends React.Component {

    render() {
        const { handlerCancel, functionChange ,  functionSave, categoryModel} = this.props;
        return (
            <div>
                <p>Create Data</p>
                <Form>

           <Label>Name</Label>
                  <Input type="text" class="form-control" placeholder="Name" value={categoryModel.name} onChange={functionChange('name')}/> <br />
                <Label>Activity</Label>
                <Input type="text" class="form-control" placeholder="Activity"  value={categoryModel.activity} onChange={functionChange('activity')}/> <br />
                <Label>Date</Label> <br/>
                <Input type="date" class="form-control" value={categoryModel.date} onChange={functionChange('date')}/> <br />
                <br/>
                <Button type="submit" color="primary" class="btn btn-info pull-right"  onClick={functionSave}>Save Change</Button> <br/><br/>
                <Button type="submit" color="danger" class="btn btn-default" onClick={handlerCancel}>Cancel</Button>
                </Form>

</div>
        )
    }
}

export default FormInput
