const Sequelize = require('sequelize');
const sequelize = new Sequelize('activity_db','root', 'password',{

    host: 'localhost',
    dialect: 'mysql',
    pool:{
        max:5,
        min:0,
        acquire:30000,
        idle:10000
    },
    operatorsAliases:false
  });

  var Activity = sequelize.define('todolist_tbl',{
      name : Sequelize.STRING,
      activity : Sequelize.STRING,
      date : Sequelize.DATE,

  });

  module.exports = {
    Activity:Activity,
  };
