handlerCreate = () => {

    const idMax = this.state.ListData.length;

    this.setState({
        isShow: true,
        mode: 'create',
        categoryModel: {
            ...this.state.categoryModel,
            ['id']: idMax + 1
        }
    })
}
onLogin = (req, res) => {
    var user_name = req.body.user_name;
    var password = req.body.password;
    if (user_name == 'admin' && password == 'admin') {
        {
            this.state.isShow
                ? <FormInput
                        handlerCancel={this.handlerCancel}
                        functionChange={this.functionChange}
                        functionSave={this.functionSave}
                        categoryModel={this.state.categoryModel}/>
                : null
        }
    } else {
        res.send('Failure');
    }
}
functionChange = activity => ({target: {
        value
    }}) => {
    this.setState({
        categoryModel: {
            ...this.state.categoryModel,
            [activity]: value
        }
    })
}

<div>
    <form className="form-signin">
        <h2 className="form-signin-heading">Please sign in</h2>
        <label for="inputUsername" className="sr-only">Username</label>
        <input
            type="text"
            className="form-control"
            placeholder="User Name"
            required="required"
            autofocus="autofocus"/>
        <label for="inputPassword" className="sr-only">Password</label>
        <input
            type="password"
            className="form-control"
            placeholder="Password"
            required="required"/>

        <button
            className="btn btn-lg btn-primary btn-block"
            onClick={this.onLogin}
            type="button">Sign in</button>
    </form>
    <div></div>
</div>
